function validateEmail(a){return/^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i.test(a)}function validateCell(a){return/^\(?([2-9]{1}[0-9]{2})\)?[-]?([2-9]{1}[0-9]{2})[-]?([0-9]{4})$/.test(a)}function error_message(a){$("#login-error").html(a).fadeIn("slow");return!1}function signup_error_message(a){$("#signup-error").html(a).fadeIn("slow");return!1}function has_number(a){matches=a.match(/\d+/g);return null!=matches?!0:!1}
function addDashes(a){var b="",c="",d="";a.value=a.value.replace(/(\D+)/g,"");b=a.value.substr(0,3);4>a.value.length||(c=a.value.substr(3,3),7>a.value.length?a.value=b+"-"+c:(d=a.value.substr(6,4),a.value=b+"-"+c+"-"+d))};
